import { HANDLE_CURRENCIES } from '../actions';

const INITIAL_STATE = {
  currencies: [],
  expenses: [],
};

const wallet = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case HANDLE_CURRENCIES:
    return {
      ...state,
      currencies: action.currencies,
    };

  default:
    return state;
  }
};

export default wallet;
