import { HANDLE_EMAIL } from '../actions';

const INITIAL_STATE = {
  user: {
    email: '',
  },
};

const user = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case HANDLE_EMAIL:
    return {
      ...state,
      user: { email: action.email },
    };

  default:
    return state;
  }
};

export default user;
