import React from 'react';
import { connect } from 'react-redux';
import { fetchCurrencies, handleInputs } from '../actions';
// import PropTypes from 'prop-types';

class Form extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      description: '',
      currency: '',
      method: '',
      tag: '',
    };
    this.buttonClick = this.buttonClick.bind(this);
  }

  componentDidMount() {
    const { fetchDispatch } = this.props;
    fetchDispatch();
  }

  handleInputs({ name, value }) {
    this.setState({
      [name]: value,
    });
  }

  valueInput() {
    return (
      <label htmlFor="value-input">
        Valor:
        <input
          data-testid="value-input"
          type="number"
          id="value-input"
          name="value"
          onChange={ ({ target }) => this.handleInputs(target) }
        />
      </label>
    );
  }

  descriptionInput() {
    return (
      <label htmlFor="description-input">
        Descrição:
        <input
          data-testid="description-input"
          type="text"
          id="description-input"
          name="description"
          onChange={ ({ target }) => this.handleInputs(target) }
        />
      </label>
    );
  }

  currenciesInput() {
    const { currencies } = this.props;
    return (
      <label htmlFor="currency-input">
        Moedas:
        <select
          data-testid="currency-input"
          id="currency-input"
          name="currency"
          onChange={ ({ target }) => this.handleInputs(target) }
        >
          <option>Selecione</option>
          {currencies.map((currency) => (
            <option
              data-testid={ currency }
              key={ currency }
              value={ currency }
            >
              { currency }
            </option>
          ))}
        </select>
      </label>
    );
  }

  methodInput() {
    return (
      <label htmlFor="method-input">
        Método de pagamento:
        <select
          data-testid="method-input"
          id="method-input"
          name="method"
          onChange={ ({ target }) => this.handleInputs(target) }
        >
          <option>Selecione</option>
          <option>Dinheiro</option>
          <option>Cartão de crédito</option>
          <option>Cartão de débito</option>
        </select>
      </label>
    );
  }

  tagInput() {
    return (
      <label htmlFor="tag-input">
        Categoria:
        <select
          data-testid="tag-input"
          id="tag-input"
          name="tag"
          onChange={ ({ target }) => this.handleInputs(target) }
        >
          <option>Selecione</option>
          <option>Alimentação</option>
          <option>Lazer</option>
          <option>Trabalho</option>
          <option>Transporte</option>
          <option>Saúde</option>
        </select>
      </label>
    );
  }

  buttonInput() {
    return (
      <button type="button" onClick={ this.buttonClick }>
        Adicionar despesa
      </button>
    );
  }

  async fetchRates() {
    const endpoint = 'https://economia.awesomeapi.com.br/json/all';
    const response = await fetch(endpoint);
    const object = await response.json();
    console.log(object);
  }

  buttonClick() {
    const { value, description, currency, method, tag } = this.state;
    const { stateDispatch } = this.props;
    const obj = {
      value,
      description,
      currency,
      method,
      tag,
      exchangeRates: {},
    };
    stateDispatch(obj);
  }

  render() {
    return (
      <form>
        <fieldset>
          { this.valueInput() }
          { this.descriptionInput() }
          { this.currenciesInput() }
          { this.methodInput() }
          { this.tagInput() }
          { this.buttonInput() }
        </fieldset>
      </form>
    );
  }
}

const mapStateToProps = (state) => ({
  currencies: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  fetchDispatch: () => dispatch(fetchCurrencies()),
  stateDispatch: (obj) => dispatch(handleInputs(obj)),
});

// Form.propTypes = {

// };
export default connect(mapStateToProps, mapDispatchToProps)(Form);
